package arrays;

public class MultiDimentionalCharArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a[][]=new int[2][2];
		
		a[0][0]=1;
		a[0][1]=2;
		a[1][0]=3;
		a[1][1]=4;
		
		System.out.print(a[0][0]+" ");
		System.out.println(a[0][1]);
		System.out.print(a[1][0]+" ");
        System.out.println(a[1][1]);
	}

}
